mapper.select:
select
  id, nav_type_id, nav_name, nav_desc, nav_url, nav_logo, nav_shared, nav_tag, create_by, create_time, last_update_by, last_update_time, is_delete
from nav_info


        and id = #{id}


        and nav_type_id = #{navTypeId}


        and nav_name = #{navName}


        and nav_desc = #{navDesc}


        and nav_url = #{navUrl}


        and nav_logo = #{navLogo}


        and nav_shared = #{navShared}


        and nav_tag = #{navTag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}


        and is_delete = #{isDelete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from nav_info


        and id = #{id}


        and nav_type_id = #{navTypeId}


        and nav_name = #{navName}


        and nav_desc = #{navDesc}


        and nav_url = #{navUrl}


        and nav_logo = #{navLogo}


        and nav_shared = #{navShared}


        and nav_tag = #{navTag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}


        and is_delete = #{isDelete}
